package com.perscholas.java_basics;

public class UnaryOperato {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = +1;
		// sum is now 1
		System.out.println(sum);

		sum--;
		// sum is now 0
		System.out.println(sum);

		sum++;
		// sum is now 1
		System.out.println(sum);

		sum = -sum;
		// sum is now -1
		System.out.println(sum);

		boolean result = false;
		// false
		System.out.println(result);
		// true
		System.out.println(!result);

	}

}
